mapper.select:
		SELECT * FROM Product WHERE 1=1

 		AND category_name = #{category_name}


	    AND trade_method = #{trade_method}

	    AND pay_method = #{pay_method}


	 		AND trade_status = '예약 중' OR '거래 완료'

	        AND trade_status = #{trade_status}


	        AND product_price BETWEEN #{startPrice} AND #{endPrice}



	        AND product_price > #{startPrice}




	        AND product_price <= #{endPrice}


		ORDER BY created_datetime DESC
